mapper.select:
select
s.* , u.user_name as uname
from t_sale_chance s
left join t_user u
on s.assign_man = u.id
where s.is_valid =1


    and s.customer_name like concat('%',#{customerName},'%')


    and s.create_man = #{createMan}


    and s.state = #{state}



    and s.dev_Result = #{devResult}


    and s.assign_man = #{assignMan}

mapper.update:
update t_sale_chance set is_valid = 0 where
id in

    #{id}
